export const getRecommendations = async (user) => {
  const { address, preferences, following } = user; // Lấy danh sách following từ user

  // Lấy danh sách tất cả restaurant & user thường
  const allRestaurants = await fetch('http://localhost:5000/api/user/restaurants').then(res => res.json());
  console.log("recommend res:", allRestaurants);

  const allNormalUsers = await fetch('http://localhost:5000/api/user/normal-users').then(res => res.json());
  console.log("recommend normal:", allNormalUsers);

  // Loại bỏ những người đã follow
  const unfollowedNormalUsers = allNormalUsers.filter(normalUser => !following.includes(normalUser._id));

  // Lọc người dùng có sở thích chung
  let filteredNormalUsers = unfollowedNormalUsers.filter(normalUser =>
    preferences.some(pref => normalUser.preferences.includes(pref))
  );
  console.log("filtered normal users:", filteredNormalUsers);

  let filteredRestaurants = allRestaurants.filter(restaurant =>
    preferences.some(pref => restaurant.foodTypes.includes(pref))
  );

  // Nếu không có sở thích chung, chọn ngẫu nhiên
  if (filteredRestaurants.length === 0) {
    filteredRestaurants = allRestaurants.sort(() => Math.random() - 0.5).slice(0, 5);
  } else {
    filteredRestaurants = filteredRestaurants.sort((a, b) => b.rating - a.rating).slice(0, 5);
  }

  if (filteredNormalUsers.length === 0) {
    filteredNormalUsers = unfollowedNormalUsers.sort(() => Math.random() - 0.5).slice(0, 5);
  } else {
    filteredNormalUsers = filteredNormalUsers.sort((a, b) => b.points - a.points).slice(0, 5);
  }

  return { restaurants: filteredRestaurants, normalUsers: filteredNormalUsers };
};
