import React, { useRef, useState } from "react";
import { useForm } from "react-hook-form";
import { Popover, Typography } from "@mui/material";
import { FaSearch } from "react-icons/fa";
import axios from "axios";
import TextInput from "../components/TextInput"; // ðŸ”¹ DÃ¹ng láº¡i TextInput nhÆ° trong SearchBar
import CustomBtn from "../components/CustomBtn"; // ðŸ”¹ DÃ¹ng láº¡i CustomBtn
import FriendCard from "../components/friend/FriendCard";

function SearchPage() {
    const {
        register,
        handleSubmit,
        setValue
    } = useForm({ mode: "onChange" });

    const inputRef = useRef(null); // ðŸ”¹ Ref cho input Ä‘á»ƒ láº¥y kÃ­ch thÆ°á»›c vÃ  focus
    const [anchorEl, setAnchorEl] = useState(null);
    const [searchResults, setSearchResults] = useState([]);
    const [inputWidth, setInputWidth] = useState(0);

    const handleOpenPopover = () => {
        setAnchorEl(inputRef.current);
        if (inputRef.current?.offsetWidth !== inputWidth) {
            setInputWidth(inputRef.current?.offsetWidth);
        }
    };

    const handleClosePopover = () => {
        setAnchorEl(null);
    };

    const onSubmit = async (data) => {
        await handleSearch(data.search);
    };

    const handleSearch = async (keyword) => {
        if (!keyword.trim()) return;

        try {
            const response = await axios.post("http://localhost:5000/api/user/search", {
                keyword
            });

            if (response.status === 200) {
                setSearchResults(response.data);
            } else {
                setSearchResults([]);
            }
        } catch (error) {
            console.error("Search error:", error);
            setSearchResults([]);
        }
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)} className="w-full flex items-center">
            <TextInput
                type="text"
                name="search"
                placeholder="Search"
                register={register("search")}
                styles="rounded-full w-full bg-gray-100 rounded-r-none h-10"
                ref={inputRef}
            />
            <CustomBtn label={
                <div className="bg-blue-600 hover:bg-blue-500 flex justify-center items-center h-10 px-2 rounded-r-full cursor-pointer"
                    onClick={handleOpenPopover}>
                    <FaSearch size={20} />
                </div>
            } type="submit" />

            <Popover
                open={Boolean(anchorEl)}
                anchorEl={anchorEl}
                onClose={handleClosePopover}
                anchorOrigin={{ vertical: "bottom", horizontal: "left" }}
                transformOrigin={{ vertical: "top", horizontal: "right" }}
            >
                <div className="max-h-[200px] overflow-y-auto no-scrollbar p-2" style={{ width: `${inputWidth}px` }}>
                    {searchResults.length ? (
                        searchResults.map((user, index) => (
                            <FriendCard friendInfo={user} key={index} />
                        ))
                    ) : (
                        <Typography sx={{ p: 1, cursor: "pointer" }}>No user found</Typography>
                    )}
                </div>
            </Popover>
        </form>
    );
}

export default SearchPage;
