const UnfollowDialog = ({ open, handleClose, user }) => {
  const dispatch = useDispatch();

  const handleUnfollow = async () => {
    if (!user) return;

    try {
      const token = localStorage.getItem("token");
      console.log("Token g·ª≠i ƒëi:", token); // üü¢ Log token ƒë·ªÉ ki·ªÉm tra

      fetch(`http://localhost:5000/api/user/unfollow/${user._id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
        .then((res) => res.json())
        .then(console.log)
        .catch(console.error);
      

      handleClose();
    } catch (error) {
      console.error("L·ªói khi unfollow:", error);
    }
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Unfollow {user?.username}?</DialogTitle>
      <DialogContent>
        B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën b·ªè theo d√µi {user?.username}?
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="primary">Cancel</Button>
        <Button onClick={handleUnfollow} color="secondary">Unfollow</Button>
      </DialogActions>
    </Dialog>
  );
};

export default UnfollowDialog;
