import React, { useEffect, useState } from "react";
import "./Sugesstions.css";
import AccountUser from "../components/AccountUser";
import { Avatar, CircularProgress } from "@mui/material";
import { getRecommendations } from "../utils/recommendations";

function Sugesstions() {
    const [suggestions, setSuggestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [followedAccounts, setFollowedAccounts] = useState(new Set());

    // Lấy danh sách đã follow từ API
    const fetchFollowedUsers = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/user/followed", {
                headers: { "Authorization": `Bearer ${localStorage.getItem("token")}` }
            });

            if (response.ok) {
                const followedUsersData = await response.json();
                const followedUsers = followedUsersData.following.map(user => user._id);
                setFollowedAccounts(new Set(followedUsers));
            } else {
                console.error("Không lấy được danh sách đã follow.");
            }
        } catch (error) {
            console.error("Lỗi khi lấy danh sách follow:", error);
        }
    };

    // Lấy danh sách gợi ý từ API
    const fetchSuggestions = async () => {
        setLoading(true);
        try {
            const user = JSON.parse(localStorage.getItem("user"));
            const recommendations = await getRecommendations(user);
            const restaurantSuggestions = recommendations.restaurants || [];
            const normalUserSuggestions = recommendations.normalUsers || [];

            setSuggestions([...restaurantSuggestions, ...normalUserSuggestions].slice(0, 5));
        } catch (error) {
            console.error("Lỗi khi lấy gợi ý:", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFollowedUsers();  // Lấy danh sách đã follow khi load trang
        fetchSuggestions();
    }, []);

    // Xử lý follow
    const handleFollow = async (accountId) => {
        try {
            const response = await fetch(`http://localhost:5000/api/user/follow/${accountId}`, {
                method: "POST",
                headers: {
                    "Authorization": `Bearer ${localStorage.getItem("token")}`,
                    "Content-Type": "application/json"
                },
            });

            if (response.ok) {
                setFollowedAccounts((prev) => new Set([...prev, accountId])); // ✅ Cập nhật UI ngay lập tức
            } else {
                console.error("Follow thất bại:", await response.json());
            }
        } catch (error) {
            console.error("Follow thất bại:", error);
        }
    };

    return (
        <div className="sugesstions">
            <div className="account__username">
                <div className="account__left">
                    <span className="avatar__profile">
                        <Avatar style={{ width: "50px", height: "50px" }}>R</Avatar>
                    </span>
                    <div className="username__info">
                        <span className="username__profile">_rin211</span>
                        <span className="profilename">Huy Thai</span>
                    </div>
                </div>
                <button className="switch__button">Switch</button>
            </div>

            <div className="sugesstions__title">Suggestions for you</div>

            {loading ? (
                <div className="loading-spinner">
                    <CircularProgress />
                </div>
            ) : (
                <div className="sugesstions__usernames">
                    {suggestions.length === 0 ? (
                        <p>No suggestions available.</p>
                    ) : (
                        suggestions.map((user) => (
                            <AccountUser
                                key={user._id}
                                username={user.username}
                                nickname={user.nickname || "User"}
                                isFollowed={followedAccounts.has(user._id)}
                                onFollow={() => handleFollow(user._id)}
                            />
                        ))
                    )}
                </div>
            )}
        </div>
    );
}

export default Sugesstions;
