import React, { useState, useEffect } from "react";
import {
  Modal,
  Box,
  Avatar,
  Typography,
  IconButton,
  InputBase,
  Divider,
} from "@mui/material";
import TelegramIcon from "@mui/icons-material/Telegram";
import {
  Facebook,
  WhatsApp,
  Email,
  Close,
  CopyAll,
  Messenger,
} from "@mui/icons-material";

const ShareModal = ({ open, onClose, followingUsers }) => {
  const [searchTerm, setSearchTerm] = useState("");

  // Mock data for following users (replace with real data from your app)
  const mockFollowingUsers = [
    { id: "1", username: "NN", avatar: "https://via.placeholder.com/40" },
    { id: "2", username: "iza_**_e9", avatar: "https://via.placeholder.com/40" },
    { id: "3", username: "Uyen Phuong", avatar: "https://via.placeholder.com/40" },
    { id: "4", username: "Hieu Lam", avatar: "https://via.placeholder.com/40" },
  ];

  const users = followingUsers || mockFollowingUsers;

  // Filter users based on search term
  const filteredUsers = users.filter((user) =>
    user.username.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Modal open={open} onClose={onClose}>
      <Box
        sx={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: 300,
          bgcolor: "background.paper",
          boxShadow: 24,
          p: 2,
          borderRadius: 2,
          outline: "none",
        }}
      >
        <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center", mb: 2 }}>
          <Typography variant="h6" component="h2">
            Share
          </Typography>
          <IconButton onClick={onClose}>
            <Close />
          </IconButton>
        </Box>

        <InputBase
          placeholder="Search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{
            width: "100%",
            p: 1,
            border: "1px solid #ccc",
            borderRadius: 4,
            mb: 2,
          }}
        />

        {filteredUsers.map((user) => (
          <Box key={user.id} sx={{ display: "flex", alignItems: "center", p: 1 }}>
            <Avatar src={user.avatar} sx={{ width: 40, height: 40, mr: 2 }} />
            <Typography variant="body1">{user.username}</Typography>
          </Box>
        ))}

        <Divider sx={{ my: 2 }} />

        <Box sx={{ display: "flex", justifyContent: "space-around" }}>
          <IconButton color="primary">
            <CopyAll />
          </IconButton>
          <IconButton color="primary">
            <Facebook />
          </IconButton>
          <IconButton color="primary">
            <Messenger />
          </IconButton>
          <IconButton color="primary">
            <WhatsApp />
          </IconButton>
          <IconButton color="primary">
            <Email />
          </IconButton>
        </Box>
      </Box>
    </Modal>
  );
};

export default ShareModal;
