import React from "react";
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Avatar, Typography } from "@mui/material";
import axios from "axios";

const UnfollowDialog = ({ open, handleClose, user, onUnfollow }) => {
  const handleUnfollow = async () => {
    try {
      await axios.post(`http://localhost:5000/api/unfollow/${user._id}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      onUnfollow(user._id); // Cập nhật UI sau khi unfollow
      handleClose();
    } catch (error) {
      console.error("Lỗi khi unfollow:", error);
    }
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Xác nhận Unfollow</DialogTitle>
      <DialogContent>
        <Avatar src={user.avatar} sx={{ width: 56, height: 56, margin: "auto" }} />
        <Typography align="center" sx={{ mt: 2 }}>
          Bạn có chắc chắn muốn unfollow <strong>{user.username}</strong> không?
        </Typography>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="primary">Cancel</Button>
        <Button onClick={handleUnfollow} color="secondary" variant="contained">Unfollow</Button>
      </DialogActions>
    </Dialog>
  );
};

export default UnfollowDialog;
