import React, { useRef, useState } from "react";
import axios from "axios";
import "./SearchPage.css";
import SearchIcon from "@mui/icons-material/Search";
import CancelIcon from "@mui/icons-material/Cancel";
import FriendCard from "../components/friend/FriendCard";

function SearchPage() {
    const [isFocused, setIsFocused] = useState(false);
    const [searchResults, setSearchResults] = useState([]);
    const searchRef = useRef(""); // ðŸ”¹ DÃ¹ng useRef thay vÃ¬ useState

    const handleFocus = () => {
        setIsFocused(true);
    };

    const handleBlur = (e) => {
        if (e.target.value === "") {
            setIsFocused(false);
        }
    };

    const handleClearAll = () => {
        setSearchResults([]);
    };

    const handleSearch = async (e) => {
        searchRef.current = e.target.value; // ðŸ”¹ Cáº­p nháº­t giÃ¡ trá»‹ useRef
        if (searchRef.current.trim() === "") {
            setSearchResults([]);
            return;
        }

        try {
            console.log("Fetching data...");
            const response = await axios.post("http://localhost:5000/api/user/search", {
                keyword: searchRef.current
            });
            console.log("Search result:", response.data);
            setSearchResults(response.data);
        } catch (error) {
            console.error("Search error:", error);
        }
    };

    return (
        <div className="search__tab">
            <div className="tab__header">
                <span>Search</span>
            </div>
            <div className="tab__main">
                <div className="input__search0">
                    <div className="input__search1">
                        {isFocused ? (
                            <CancelIcon className="cancel-icon" onClick={() => { searchRef.current = ""; handleSearch({ target: { value: "" } }); }} />
                        ) : (
                            <SearchIcon className="search-icon" />
                        )}
                        <input
                            type="text"
                            placeholder="Search"
                            defaultValue={searchRef.current} // ðŸ”¹ DÃ¹ng defaultValue thay vÃ¬ value
                            onChange={handleSearch}
                            onFocus={handleFocus}
                            onBlur={handleBlur}
                        />
                    </div>
                </div>
                <hr />
                <div className="history__search">
                    <div className="history__title">
                        <span>Results</span>
                        <button className="clear__history" onClick={handleClearAll}>
                            Clear all
                        </button>
                    </div>
                    {searchResults.length > 0 ? (
                        searchResults.map((user, index) => (
                            <FriendCard key={index} friendInfo={user} isSearchList={true} />
                        ))
                    ) : (
                        <p>No results found.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default SearchPage;
