import React, { useRef, useState } from "react";
import axios from "axios";
import "./SearchPage.css";
import SearchIcon from "@mui/icons-material/Search";
import CancelIcon from "@mui/icons-material/Cancel";
import FriendCard from "../components/friend/FriendCard";

function SearchPage() {
    const [isFocused, setIsFocused] = useState(false);
    const [searchResults, setSearchResults] = useState([]);
    const [searchTerm, setSearchTerm] = useState(""); // ðŸ”¹ DÃ¹ng state Ä‘á»ƒ cáº­p nháº­t giao diá»‡n
    const inputRef = useRef(null); // ðŸ”¹ useRef Ä‘á»ƒ giá»¯ giÃ¡ trá»‹ input mÃ  khÃ´ng re-render

    const handleFocus = () => setIsFocused(true);
    const handleBlur = (e) => {
        if (e.target.value === "") setIsFocused(false);
    };

    const handleClearAll = () => setSearchResults([]);

    const handleSearch = async (e) => {
        const keyword = e.target.value;
        inputRef.current = keyword;
        setSearchTerm(keyword); // ðŸ”¹ Cáº­p nháº­t state Ä‘á»ƒ re-render icon "x"

        if (keyword.trim() === "") {
            setSearchResults([]);
            return;
        }

        try {
            console.log("Fetching data...");
            const response = await axios.post("http://localhost:5000/api/user/search", { keyword });
            console.log("Search result:", response.data);
            setSearchResults(response.data);
        } catch (error) {
            console.error("Search error:", error);
        }
    };

    return (
        <div className="search__tab">
            <div className="tab__header">
                <span>Search</span>
            </div>
            <div className="tab__main">
                <div className="input__search0">
                    <div className="input__search1">
                        {searchTerm ? ( // ðŸ”¹ Kiá»ƒm tra searchTerm thay vÃ¬ isFocused
                            <CancelIcon className="cancel-icon" onClick={() => {
                                inputRef.current = "";
                                setSearchTerm(""); // ðŸ”¹ Cáº­p nháº­t UI
                                handleSearch({ target: { value: "" } });
                            }} />
                        ) : (
                            <SearchIcon className="search-icon" />
                        )}
                        <input
                            type="text"
                            placeholder="Search"
                            value={searchTerm} // ðŸ”¹ DÃ¹ng state Ä‘á»ƒ hiá»ƒn thá»‹ UI
                            onChange={handleSearch}
                            onFocus={handleFocus}
                            onBlur={handleBlur}
                        />
                    </div>
                </div>
                <hr />
                <div className="history__search">
                    <div className="history__title">
                        <span>Results</span>
                        <button className="clear__history" onClick={handleClearAll}>
                            Clear all
                        </button>
                    </div>
                    {searchResults.length > 0 ? (
                        searchResults.map((user, index) => (
                            <FriendCard key={index} friendInfo={user} isSearchList={true} />
                        ))
                    ) : (
                        <p>No results found.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default SearchPage;
