import React, { useState } from "react";
import axios from "axios";
import "./SearchPage.css";
import AccountUser from "../components/AccountUser";
import SearchIcon from "@mui/icons-material/Search";
import CancelIcon from "@mui/icons-material/Cancel";

function SearchPage() {
    const [isFocused, setIsFocused] = useState(false);
    const [searchResults, setSearchResults] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");

    const handleFocus = () => {
        setIsFocused(true);
    };

    const handleBlur = (e) => {
        if (e.target.value === "") {
            setIsFocused(false);
        }
    };

    const handleClearAll = () => {
        setSearchResults([]);
    };

    const handleSearch = async (e) => {
        const keyword = e.target.value;
        setSearchTerm(keyword);

        if (keyword.trim() === "") {
            setSearchResults([]);
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api//search", {
                keyword: keyword
            });

            setSearchResults(response.data); 
        } catch (error) {
            console.error("Search error:", error);
        }
    };

    return (
        <div className="search__tab">
            <div className="tab__header">
                <span>Search</span>
            </div>
            <div className="tab__main">
                <div className="input__search0">
                    <div className="input__search1">
                        {isFocused ? (
                            <CancelIcon className="cancel-icon" onClick={() => setSearchTerm("")} />
                        ) : (
                            <SearchIcon className="search-icon" />
                        )}
                        <input
                            type="text"
                            placeholder="Search"
                            value={searchTerm}
                            onChange={handleSearch}
                            onFocus={handleFocus}
                            onBlur={handleBlur}
                        />
                    </div>
                </div>
                <hr />
                <div className="history__search">
                    <div className="history__title">
                        <span>Results</span>
                        <button className="clear__history" onClick={handleClearAll}>
                            Clear all
                        </button>
                    </div>
                    {searchResults.length > 0 ? (
                        searchResults.map((user, index) => (
                            <AccountUser key={index} username={user.username} nickname={user.nickname} isSearchList={true} />
                        ))
                    ) : (
                        <p>No results found.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default SearchPage;
