import "./HeaderProfile.css";
import React, { useState } from "react";
import { Avatar, Link, Dialog, DialogTitle, DialogContent, List, ListItem, ListItemText } from "@mui/material";
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import axios from "axios";

function HeaderProfile({ user, userId }) {
  const navigate = useNavigate();
  const [isFollowing, setIsFollowing] = useState(false);
  const userStorage = useSelector((state) => state.user.user) || {};
  const [followingList, setFollowingList] = useState([]); // LÆ°u danh sÃ¡ch following
  const [open, setOpen] = useState(false); // Tráº¡ng thÃ¡i má»Ÿ/Ä‘Ã³ng modal

  const handleEditProfile = () => {
    navigate('/EditProfilePage');
  };

  const handleFollow = async (accountId) => {
    try {
      await axios.post(`http://localhost:5000/api/follow/${accountId}`, {}, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` },
      });
    } catch (error) {
      console.error('Follow tháº¥t báº¡i:', error);
    }
  };

  // ðŸŸ¢ Xá»­ lÃ½ khi click vÃ o following count
  const handleShowFollowing = async () => {
    console.log("Click vÃ o following!");
    try {
      const token = localStorage.getItem("token"); // Láº¥y token tá»« localStorage
      if (!token) {
        console.error("KhÃ´ng tÃ¬m tháº¥y token!");
        return;
      }
  
      const response = await axios.get(`http://localhost:5000/api/user/${user._id}/following`, {
        headers: { Authorization: `Bearer ${token}` }, // ThÃªm token vÃ o headers
      });
  
      console.log("Following List:", response.data);
      setFollowingList(response.data);
      setOpen(true);
    } catch (error) {
      console.error("Lá»—i khi láº¥y danh sÃ¡ch following:", error);
    }
  };
  return (
    <div className="header__profile">
      <div className="header__left">
        <Avatar
          alt="Avatar"
          src="https://th.bing.com/th/id/OIP.9ccjBmE32LmJ02sy28_xwAHaK-?rs=1&pid=ImgDetMain"
          sx={{ width: 150, height: 152 }}
        />
      </div>
      <div className="header__right">
        <div className="header__1">
          <span>{user.username}</span>
          {userId === userStorage._id ? (
            <button variant="contained" color="primary" onClick={handleEditProfile}>
              Edit Profile
            </button>
          ) : (
            <button variant="contained" color={isFollowing ? "secondary" : "primary"} onClick={handleFollow}>
              {isFollowing ? "Following" : "Follow"}
            </button>
          )}
        </div>
        <div className="header__2">
          <div className="header2__content">
            <span>9 </span> posts
          </div>
          <div className="header2__content">
            <span>{user.followerCount}</span> followers
          </div>
          <div className="header2__content" onClick={handleShowFollowing} style={{ cursor: "pointer", color: "blue" }}>
            <span>{user.followingCount}</span> following
          </div>
        </div>
        <div className="header__3">
          <span>Huy Thai</span>
          <span className="header3__job">Food reviewer</span>
          <span>If you want to watch my clips ðŸŽ¦ in your language ðŸŒŽ </span>
          <a href="https://www.instagram.com/" target="_blank" rel="noopener noreferrer">https://www.instagram.com/</a>
        </div>
      </div>

      {/* ðŸŸ¢ Modal hiá»ƒn thá»‹ danh sÃ¡ch following */}
      <Dialog open={open} onClose={() => setOpen(false)}>
        <DialogTitle>Following List</DialogTitle>
        <DialogContent>
          <List>
            {followingList.length > 0 ? (
              followingList.map((followedUser) => (
                <ListItem key={followedUser._id}>
                  <Avatar src={followedUser.avatar} />
                  <ListItemText primary={followedUser.username} />
                </ListItem>
              ))
            ) : (
              <p>KhÃ´ng cÃ³ following nÃ o.</p>
            )}
          </List>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default HeaderProfile;
