import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams } from 'react-router-dom'
import axios from "axios";
import { Box, CircularProgress, Button, Typography } from "@mui/material";
import Grid2 from "@mui/material/Grid2";
import ProfileTabs from "../components/ProfileTabs";
import FavouriteMap from "../components/FavouriteMap";
import ProfilePost from "../components/ProfilePost";
import "./ProfilePage.css";

function ProfilePage() {
  const { id } = useParams(); // Lấy ID từ URL
  const dispatch = useDispatch();
  const userStorage = useSelector((state) => state.user.user); // Lấy user đang đăng nhập từ Redux
  const [type, setType] = useState("posts");
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [user, setUser] = useState(null); // Dữ liệu user (có thể là user hiện tại hoặc user khác)
  const [relationshipStatus, setRelationshipStatus] = useState(""); // Trạng thái quan hệ (following/follow)

  // Kiểm tra xem đây có phải là profile của user đang đăng nhập không
  const isOwnProfile = userStorage?.user?._id === id;

  // Dữ liệu user mặc định nếu là profile của user hiện tại
  const defaultUserData = {
    name: userStorage?.user?.firstName + " " + userStorage?.user?.lastName || "Thái Tăng Huy",
    job: userStorage?.user?.profession || "Food reviewer",
    followers: userStorage?.user?.followers?.length || 1234,
    following: userStorage?.user?.following?.length || 567,
    posts: data.length || 9,
    socialLinks: [
      { platform: "Facebook", url: "https://facebook.com/username" },
      { platform: "Twitter", url: "https://twitter.com/username" },
      { platform: "LinkedIn", url: "https://linkedin.com/in/username" },
    ],
  };

  // Lấy dữ liệu user và bài post
  useEffect(() => {
    const fetchUserData = async () => {
      setIsLoading(true);
      try {
        if (!isOwnProfile) {
          // Gọi API để lấy thông tin user khác
          const userRes = await axios.get(`http://localhost:5000/api/users/get-by-id/${id}`, {
            headers: { Authorization: `Bearer ${userStorage.token}` },
          });
          setUser(userRes.data);

          // Gọi API để kiểm tra trạng thái quan hệ
          const relationshipRes = await axios.get(
            `http://localhost:5000/api/users/relationship/${id}`,
            {
              headers: { Authorization: `Bearer ${userStorage.token}` },
            }
          );
          setRelationshipStatus(relationshipRes.data.status); // "following" hoặc "follow"
        } else {
          setUser(defaultUserData); // Sử dụng dữ liệu user hiện tại
          setRelationshipStatus(""); // Không cần trạng thái quan hệ cho profile của chính mình
        }

        // Lấy bài post
        if (type === "posts") {
          const postsRes = await axios.get(
            isOwnProfile
              ? `https://jsonplaceholder.typicode.com/photos?_limit=9`
              : `http://localhost:5000/api/posts/get-posts-by-user/${id}`,
            {
              headers: isOwnProfile ? {} : { Authorization: `Bearer ${userStorage.token}` },
            }
          );

          const updatedData = postsRes.data.map((item) => ({
            ...item,
            thumbnailUrl:
              "https://th.bing.com/th/id/OIP.F48Ta-u-I74gaRY18l_8sAHaE8?rs=1&pid=ImgDetMain",
          }));
          setData(updatedData);
        } else {
          setData([]);
        }
      } catch (error) {
        console.error("Lỗi khi lấy dữ liệu:", error);
        if (type === "posts") {
          const defaultData = Array.from({ length: 9 }, (_, index) => ({
            id: index + 1,
            thumbnailUrl:
              "https://th.bing.com/th/id/OIP.F48Ta-u-I74gaRY18l_8sAHaE8?rs=1&pid=ImgDetMain",
            title: `Default Image ${index + 1}`,
          }));
          setData(defaultData);
        }
      } finally {
        setTimeout(() => setIsLoading(false), 500);
      }
    };

    fetchUserData();
  }, [id, type, isOwnProfile, userStorage]);

  // Xử lý follow/unfollow
  const handleFollowToggle = async () => {
    try {
      if (relationshipStatus === "following") {
        // Unfollow
        await axios.post(
          `http://localhost:5000/api/users/unfollow`,
          { userId: id },
          { headers: { Authorization: `Bearer ${userStorage.token}` } }
        );
        setRelationshipStatus("follow");
      } else {
        // Follow
        await axios.post(
          `http://localhost:5000/api/users/follow`,
          { userId: id },
          { headers: { Authorization: `Bearer ${userStorage.token}` } }
        );
        setRelationshipStatus("following");
      }
    } catch (error) {
      console.error("Lỗi khi thay đổi trạng thái follow:", error);
    }
  };

  return (
    <div className="profilepage">
      {/* Header Profile */}
      <div className="profile-header">
        <div className="profile-header-content">
          <div className="profile-avatar">
            <img
              src={user?.profileUrl || "https://via.placeholder.com/150"}
              alt="Avatar"
              className="rounded-full w-full h-full object-cover"
            />
          </div>
          <div className="profile-info">
            <Typography variant="h5" className="font-bold">
              {user?.name || defaultUserData.name}
            </Typography>
            <Typography variant="body2" color="textSecondary">
              {user?.job || defaultUserData.job}
            </Typography>
            <div className="profile-stats">
              <Typography>
                <strong>{user?.posts || defaultUserData.posts}</strong> posts
              </Typography>
              <Typography>
                <strong>{user?.followers || defaultUserData.followers}</strong> followers
              </Typography>
              <Typography>
                <strong>{user?.following || defaultUserData.following}</strong> following
              </Typography>
            </div>
            {/* Nút Edit Profile hoặc Follow/Following */}
            <Box sx={{ mt: 2 }}>
              {isOwnProfile ? (
                <Button variant="outlined" color="primary">
                  Edit Profile
                </Button>
              ) : (
                <Button
                  variant={relationshipStatus === "following" ? "contained" : "outlined"}
                  color={relationshipStatus === "following" ? "secondary" : "primary"}
                  onClick={handleFollowToggle}
                >
                  {relationshipStatus === "following" ? "Following" : "Follow"}
                </Button>
              )}
            </Box>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <ProfileTabs type={type} setType={setType} />

      {/* Nội dung theo tab */}
      {type === "posts" ? (
        <Box sx={{ p: 2, display: "flex", justifyContent: "center" }}>
          {isLoading ? (
            <CircularProgress color="primary" />
          ) : (
            <Grid2
              container
              direction="row"
              sx={{
                justifyContent: "center",
                alignItems: "center",
                display: "grid",
                columnGap: 1,
                rowGap: 1,
                gridTemplateColumns: "repeat(3, 1fr)",
              }}
            >
              {data.map((item) => (
                <ProfilePost key={item.id} item={item} />
              ))}
            </Grid2>
          )}
        </Box>
      ) : (
        <Box sx={{ textAlign: "center", p: 2 }}>
          <FavouriteMap />
        </Box>
      )}
    </div>
  );
}

export default ProfilePage;
