import React, { useState } from "react";
import { Avatar, Modal, Box, Typography, IconButton, TextField } from "@mui/material";
import { Close } from "@mui/icons-material";

const ShareModal = ({ open, onClose }) => {
    const [search, setSearch] = useState("");

    // Danh sách giả lập những người user đang follow
    const followedUsers = [
        { id: 1, name: "Uyên Phương", avatar: "https://via.placeholder.com/50" },
        { id: 2, name: "Hiếu Lam", avatar: "https://via.placeholder.com/50" },
        { id: 3, name: "iza •✦•", avatar: "https://via.placeholder.com/50" },
        { id: 4, name: "Nhật Hà", avatar: "https://via.placeholder.com/50" }
    ];

    // Lọc danh sách theo từ khóa tìm kiếm
    const filteredUsers = followedUsers.filter(user =>
        user.name.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <Modal open={open} onClose={onClose}>
            <Box className="modal__box">
                <div className="modal__header">
                    <Typography variant="h6">Share</Typography>
                    <IconButton onClick={onClose}>
                        <Close />
                    </IconButton>
                </div>
                <TextField
                    fullWidth
                    variant="outlined"
                    placeholder="Search"
                    size="small"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    className="searchInput"
                />
                <div className="modal__userList">
                    {filteredUsers.map(user => (
                        <div key={user.id} className="modal__user">
                            <Avatar src={user.avatar} />
                            <span>{user.name}</span>
                        </div>
                    ))}
                </div>
            </Box>
        </Modal>
    );
};

export default ShareModal;
