import React, { useEffect, useState } from "react";
import "./Sugesstions.css";
import AccountUser from "../components/AccountUser";
import { Avatar, CircularProgress } from "@mui/material";
import { getRecommendations } from "../utils/recommendations";
import { useSelector } from "react-redux";

function Sugesstions() {
  // Lấy user từ Redux; đảm bảo user có trường following (nếu chưa có, dùng mảng rỗng)
  const userFromRedux = useSelector((state) => state.user.user) || {};
  const currentUser = { ...userFromRedux, following: userFromRedux.following || [] };

  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [followedAccounts, setFollowedAccounts] = useState(new Set());

  // Hàm lấy danh sách đã follow từ API
  const fetchFollowedUsers = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/user/followed", {
        headers: { "Authorization": `Bearer ${localStorage.getItem("token")}` },
      });
      if (response.ok) {
        const data = await response.json();
        console.log("API Followed Users Response:", data);
        // Giả sử API trả về { following: [ ... ] }
        const followed = Array.isArray(data.following) ? data.following.map(user => user._id.toString()) : [];
        setFollowedAccounts(new Set(followed));
      } else {
        console.error("Không lấy được danh sách đã follow");
      }
    } catch (error) {
      console.error("Lỗi khi lấy danh sách follow:", error);
    }
  };

  // Hàm lấy gợi ý từ API dựa trên hàm getRecommendations của bạn
  const fetchSuggestions = async () => {
    setLoading(true);
    try {
      // Đảm bảo user có trường following từ Redux (nếu có, thì dùng nó; nếu không, sử dụng danh sách vừa fetch được)
      await fetchFollowedUsers();
      // Nếu Redux user chưa có dữ liệu followed, ta có thể cập nhật lại currentUser.following từ followedAccounts
      const updatedUser = { ...currentUser, following: Array.from(followedAccounts) };
      const recommendations = await getRecommendations(updatedUser);
      console.log("Recommendations:", recommendations);
      // getRecommendations trả về { restaurants, normalUsers }
      // Ghép 2 danh sách lại (chúng đã được lọc theo following bên trong getRecommendations nếu user.following có)
      let combined = [...(recommendations.restaurants || []), ...(recommendations.normalUsers || [])];
      // Nếu bạn muốn đảm bảo không có tài khoản đã follow (nếu Redux user chưa được cập nhật), lọc lại:
      combined = combined.filter(sugg => !followedAccounts.has(sugg._id.toString()));
      // Giới hạn 5 gợi ý
      setSuggestions(combined.slice(0, 5));
    } catch (error) {
      console.error("Lỗi khi lấy gợi ý:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (currentUser && Object.keys(currentUser).length > 0) {
      fetchSuggestions();
    }
    // Chỉ chạy một lần khi user thay đổi
  }, [currentUser]);

  const handleFollow = async (accountId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/user/follow/${accountId}`, {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("token")}`,
          "Content-Type": "application/json"
        },
      });

      if (response.ok) {
        console.log(`Đã follow user: ${accountId}`);
        // Cập nhật state local để hiển thị trạng thái Follow
        setFollowedAccounts((prev) => new Set([...prev, accountId.toString()]));
        // Nếu muốn cập nhật lại gợi ý, bạn có thể gọi fetchSuggestions() hoặc tự lọc suggestions hiện tại
        setSuggestions(prev => prev.filter(sugg => sugg._id.toString() !== accountId.toString()));
      } else {
        console.error("Follow thất bại:", await response.json());
      }
    } catch (error) {
      console.error("Follow thất bại:", error);
    }
  };

  return (
    <div className="sugesstions">
      <div className="account__username">
        <div className="account__left">
          <span className="avatar__profile">
            <Avatar style={{ width: "50px", height: "50px" }}>R</Avatar>
          </span>
          <div className="username__info">
            <span className="username__profile">{currentUser.username || "_username"}</span>
            <span className="profilename">{currentUser.name || "User Name"}</span>
          </div>
        </div>
        <button className="switch__button">Switch</button>
      </div>

      <div className="sugesstions__title">Suggestions for you</div>

      {loading ? (
        <div className="loading-spinner">
          <CircularProgress />
        </div>
      ) : (
        <div className="sugesstions__usernames">
          {suggestions.length === 0 ? (
            <p>No suggestions available.</p>
          ) : (
            suggestions.map((sugg) => (
              <AccountUser
                key={sugg._id}
                username={sugg.username}
                nickname={sugg.nickname || "User"}
                isFollowed={followedAccounts.has(sugg._id.toString())}
                onFollow={() => handleFollow(sugg._id)}
              />
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default Sugesstions;
