import React, { useState } from "react";
import axios from "axios";
import "./SearchPage.css";
import AccountUser from "../components/AccountUser";
import SearchIcon from "@mui/icons-material/Search";
import CancelIcon from "@mui/icons-material/Cancel";

function SearchPage() {
    const [isFocused, setIsFocused] = useState(false);
    const [searchResults, setSearchResults] = useState([]);
    const [searchKeyword, setSearchKeyword] = useState("");

    // Xử lý khi focus vào ô input
    const handleFocus = () => {
        setIsFocused(true);
    };

    // Xử lý khi blur khỏi ô input
    const handleBlur = (e) => {
        if (e.target.value === "") {
            setIsFocused(false);
        }
    };

    // Gọi API tìm kiếm người dùng
    const handleSearch = async (e) => {
        const keyword = e.target.value;
        setSearchKeyword(keyword);

        if (keyword.length > 2) {  // Chỉ tìm kiếm khi nhập từ 3 ký tự trở lên
            try {
                const response = await fetch("http://localhost:5000/api/users/search", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ keyword }),
                });
    
                const data = await response.json();
                setSearchResults(data);
            } catch (error) {
                console.error("Lỗi tìm kiếm:", error);
            }
        } else {
            setSearchResults([]); // Xóa kết quả khi không có từ khóa
        }
    };

    // Xóa lịch sử tìm kiếm
    const handleClearAll = () => {
        setSearchResults([]);
        setSearchKeyword("");
    };

    return (
        <div className="search__tab">
            <div className="tab__header">
                <div>
                    <span>Search</span>
                </div>
            </div>
            <div className="tab__main">
                <div className="input__search0">
                    <div className="input__search1">
                        {isFocused ? (
                            <CancelIcon className="cancel-icon" onClick={handleClearAll} />
                        ) : (
                            <SearchIcon className="search-icon" />
                        )}
                        <input
                            type="text"
                            placeholder="Search"
                            id="search-input"
                            value={searchKeyword}
                            onFocus={handleFocus}
                            onBlur={handleBlur}
                            onChange={handleSearch}
                        />
                    </div>
                </div>
                <hr />
                <div className="history__search">
                    <div className="history__title">
                        <span>Recent</span>
                        <button className="clear__history" onClick={handleClearAll}>
                            Clear all
                        </button>
                    </div>
                    {searchResults.length > 0 ? (
                        searchResults.map((user, index) => (
                            <AccountUser
                                key={index}
                                username={user.username}
                                nickname={user.tag} // Hiển thị tag thay vì nickname
                                isSearchList={true}
                            />
                        ))
                    ) : (
                        <p className="no-results">No users found</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default SearchPage;
