import React from "react";
import { avatar } from "../../assets";
import { Link } from "react-router-dom";
import { Avatar } from "@mui/material";

const FriendCard = ({ friendInfo }) => {
  return (
    <Link
      to={`/profile/${friendInfo._id}`}
      className="flex items-center w-full gap-3 px-3 py-2 mb-4 rounded-xl hover:bg-gray-300 hover:bg-opacity-50 transition"
    >
      {/* Avatar có kích thước cố định */}
      <Avatar
        src={friendInfo.avatar ? friendInfo.avatar : avatar}
        alt="404"
        sx={{ width: 48, height: 48 }} // Set kích thước avatar
      />
      
      {/* Hiển thị tên người dùng */}
      <p className="text-lg font-semibold text-gray-900 truncate min-w-0">
        {friendInfo.username}
      </p>
    </Link>
  );
};

export default FriendCard;
