import React, { useState } from "react";
import "./AccountUser.css";
import ClearIcon from '@mui/icons-material/Clear';
import { Avatar } from "@mui/material";

function AccountUser({ username, nickname, isSearchList, onClear, onFollow, isFollowed }) {
    const [isFollowing, setIsFollowing] = useState(isFollowed);

    const handleFollowClick = async () => {
        if (!isFollowing) {
            await onFollow();  // Gọi API follow
            setIsFollowing(true);  // Cập nhật UI sau khi follow thành công
        }
    };

    return (
        <div className="sugesstion__username">
            <div className="username__left">
                <span className="avatar">
                    <Avatar>{username?.charAt(0).toUpperCase()}</Avatar>
                </span>
                <div className="username__info">
                    <span className="username">{username}</span>
                    <span className="nickname">{nickname}</span>
                </div>
            </div>

            {isSearchList ? (
                <ClearIcon className="clear-icon" onClick={onClear} />
            ) : (
                <div
                    className={`button__follow ${isFollowing ? "following" : ""}`}
                    onClick={handleFollowClick}
                >
                    {isFollowing ? "Following" : "Follow"}
                </div>
            )}
        </div>
    );
}

export default AccountUser;
